// Map each calendar to a type based on name patterns
Object.entries(calendarMap).forEach(([calendarName, calendarId]) => {
  // Skip snake_case duplicates
  if (calendarName.includes('_')) return;

  const lowerName = calendarName.toLowerCase();

  // Try to categorize this calendar
  for (const [type, keywords] of Object.entries(calendarTypes)) {
    if (keywords.some(keyword => lowerName.includes(keyword))) {
      // First match for this type becomes the primary calendar for that type
      if (!detectedCalendarTypes[type]) {
