// Get contact information
const contactEmail = $('Update Contact GHL').item.json.contact.email;
const contactFirstName = $('Update Contact GHL').item.json.contact.firstName;
const contactLastName = $('Update Contact GHL').item.json.contact.lastName;

// Get AI collected information
const aiEmail = $('When Executed by AI Call Webhook').item.json.body.call.call_analysis.custom_analysis_data.ai_call_contact_email;
const aiName = $('When Executed by AI Call Webhook').item.json.body.call.call_analysis.custom_analysis_data.ai_call_contact_name;

// Initialize update object and flag
let updateNeeded = false;
let updateBody = {};

// Check if name needs to be updated
// Condition: Contact name is empty OR is the default "new" placeholder AND AI collected a valid name
if ((contactFirstName === null || contactFirstName === undefined || contactFirstName === "" || contactFirstName === "None" || contactFirstName === "new") && 
    (aiName !== null && aiName !== undefined && aiName !== "" && aiName !== "none" && aiName !== "None")) {
  updateBody.name = aiName;
  updateNeeded = true;
}

// Check if email needs to be updated
// Condition: Contact email is empty AND AI collected a valid email
if ((contactEmail === null || contactEmail === undefined || contactEmail === "" || contactEmail === "None") && 
    (aiEmail !== null && aiEmail !== undefined && aiEmail !== "" && aiEmail !== "none" && aiEmail !== "None")) {
  updateBody.email = aiEmail;
  updateNeeded = true;
}

// Return consistent structure with output key
if (updateNeeded) {
  // Convert the JSON object to a string
  return [{ output: JSON.stringify(updateBody) }];
} else {
  // Return "no update" as a string
  return [{ output: "no update" }];
}
